import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Button.jsx"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./Navbar.css";
function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);


  const handleClick = () => {
    setClick(!click);
  };
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);
  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo"  onClick={closeMobileMenu}>
          TRIP
          <FontAwesomeIcon icon={["fab", "accusoft"]} />
        </Link>
        <div className="menu-icon" onClick={handleClick}>
          <FontAwesomeIcon
            icon={click ? "times" : "bars"}
          />
        </div>
        <ul className={click?"nav-menu active": "nav-menu"}>
            <li className="nav-item">
                <Link to="/home" className="nav-links" onClick={closeMobileMenu}>home</Link>
            </li>
            <li className="nav-item" onClick={closeMobileMenu}>
                <Link to="/services" className="nav-links">services</Link>
            </li>
            <li className="nav-item" onClick={closeMobileMenu}>
                <Link to="/product" className="nav-links">product</Link>
            </li>
            <li className="nav-item">
                <Link to="/sign up" className="nav-links">Sign up</Link>
            </li>
        </ul>
        {button && <Button buttonStyle='btn--outline' >SIGN UP</Button>}
      </div>
    </nav>
  );
}

export default Navbar;
